import React, { useContext, useEffect, useState } from "react";
import { AuthContext } from "../context/AuthContext";

const normalize = (task) => {
  return {
    _id: task._id,
    content: (task.content ?? task.title ?? "").toString(),
    completed: (typeof task.completed !== "undefined") ? task.completed : (task.done ?? false),
    raw: task, 
  };
};

export default function Home() {
  const { user, logout, api } = useContext(AuthContext);
  const [tasks, setTasks] = useState([]);
  const [newTaskText, setNewTaskText] = useState("");
  const [error, setError] = useState("");

  useEffect(() => {
    if (!user) return;
    const load = async () => {
      try {
        const { data } = await api.get("/tasks");
        const normalized = Array.isArray(data) ? data.map(normalize) : [];
        setTasks(normalized);
      } catch (err) {
        console.error("fetch tasks failed", err);
        setError("Failed to load tasks");
      }
    };
    load();
  }, [user, api]);

  const addTask = async (e) => {
    e?.preventDefault();
    if (!newTaskText.trim()) return;
    try {

      const payload = { title: newTaskText.trim() };
      const { data } = await api.post("/tasks", payload);
      setTasks((s) => [...s, normalize(data)]);
      setNewTaskText("");
    } catch (err) {
      console.error("addTask error:", err);
      setError("Failed to add task");
    }
  };

  const toggleTask = async (task) => {
    try {

      const payload = {
        title: task.raw.title ?? task.content,
        done: !task.completed,

        content: task.content,
        completed: !task.completed,
      };
      const { data } = await api.put(`/tasks/${task._id}`, payload);
      setTasks((s) => s.map((t) => t._id === task._id ? normalize(data) : t));
    } catch (err) {
      console.error("toggle error:", err);
      setError("Failed to update task");
    }
  };

  const deleteTask = async (taskId) => {
    try {
      await api.delete(`/tasks/${taskId}`);
      setTasks((s) => s.filter((t) => t._id !== taskId));
    } catch (err) {
      console.error("delete error:", err);
      setError("Failed to delete task");
    }
  };

  if (!user) {
    return (
      <div className="container">
        <h2>Welcome â€” please login or register</h2>
      </div>
    );
  }

  return (
    <div className="container">
      <h2>Hello, {user.username}</h2>
      <button onClick={logout}>Logout</button>

      <form onSubmit={addTask} style={{ marginTop: "1rem" }}>
        <input
          value={newTaskText}
          onChange={(e) => setNewTaskText(e.target.value)}
          placeholder="Enter new task"
          style={{ width: "70%", padding: ".6rem", marginRight: 8 }}
        />
        <button type="submit">Add Task</button>
      </form>

      {error && <p style={{ color: "crimson" }}>{error}</p>}

      <ul style={{ listStyle: "none", padding: 0, marginTop: 16 }}>
        {tasks.map((t) => (
          <li key={t._id} style={{ marginBottom: 12, display: "flex", alignItems: "center", gap: 12 }}>
            <input
              type="checkbox"
              checked={!!t.completed}
              onChange={() => toggleTask(t)}
            />
            <span style={{ textDecoration: t.completed ? "line-through" : "none" }}>{t.content || "<no text>"}</span>

            <div style={{ marginLeft: "auto", display: "flex", gap: 8 }}>
              <button onClick={() => toggleTask(t)}>{t.completed ? "Undo" : "Complete"}</button>
              <button onClick={() => deleteTask(t._id)}>Delete</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}
